} from '@mui/material';
import {
  SportsFootball,
  Star,
  Person,
  Search,
  FilterList,
} from '@mui/icons-material';
import { motion } from 'framer-motion';
import { useQuery } from '@tanstack/react-query';
import { useAuth } from '../../hooks/useAuth';
import { teamApi } from '../../services/api';
import { Team } from '../../types';
import GradientButton from '../../components/ui/GradientButton';
import GlassCard from '../../components/ui/GlassCard';

const TeamSelection: React.FC = () => {
  const { selectTeam, user } = useAuth();
  const [selectedTeam, setSelectedTeam] = useState<Team | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedConference, setSelectedConference] = useState('All');
  const [showHumanOnly, setShowHumanOnly] = useState(false);

  const { data: teamsData, isLoading, error } = useQuery({
    queryKey: ['teams'],
    queryFn: () => teamApi.getAll({ size: 1000 }), // Get all teams
  });

  const { data: conferencesData } = useQuery({
    queryKey: ['conferences'],
    queryFn: () => teamApi.getConferences(),
  });

  const handleTeamSelect = (team: Team) => {
    setSelectedTeam(team);
  };

  const handleConfirmSelection = async () => {
    if (!selectedTeam) return;

    setIsSubmitting(true);
    try {
      selectTeam(selectedTeam);
    } catch (error) {
      console.error('Error selecting team:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const containerVariants = {
    initial: { opacity: 0 },
    animate: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
      },
    },
  };

  const itemVariants = {
    initial: { y: 20, opacity: 0 },
    animate: {
      y: 0,
      opacity: 1,
      transition: {
        type: 'spring',
        stiffness: 300,
        damping: 24,
      },
    },
  };

  if (isLoading) {
    return (
      <Box
        sx={{
          minHeight: '100vh',
          background: 'linear-gradient(135deg, #0a0e27 0%, #1a1d35 50%, #242744 100%)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        <CircularProgress size={60} />
      </Box>
    );
  }

  if (error) {
    return (
      <Box
        sx={{
          minHeight: '100vh',
          background: 'linear-gradient(135deg, #0a0e27 0%, #1a1d35 50%, #242744 100%)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          p: 2,
        }}
      >
        <Alert severity="error">
          Failed to load teams. Please try again.
        </Alert>
      </Box>
    );
  }

  const allTeams = teamsData?.data?.content || [];
  const conferences = conferencesData?.data || [];

  // Filter and search teams
  const filteredTeams = useMemo(() => {
    let filtered = allTeams;

    // Apply search filter
    if (searchTerm.trim()) {
      const search = searchTerm.toLowerCase();
      filtered = filtered.filter(team =>
        team.name.toLowerCase().includes(search) ||
        team.coach?.toLowerCase().includes(search) ||
        team.conference?.toLowerCase().includes(search)
      );
    }

    // Apply conference filter
    if (selectedConference !== 'All') {
      filtered = filtered.filter(team => team.conference === selectedConference);
    }

    // Apply human-only filter
    if (showHumanOnly) {
      filtered = filtered.filter(team => team.isHuman);
    }

    return filtered;
  }, [allTeams, searchTerm, selectedConference, showHumanOnly]);

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0a0e27 0%, #1a1d35 50%, #242744 100%)',
        pt: 4,
        pb: 4,
      }}
    >
      <Container maxWidth="lg">
        <motion.div
          variants={containerVariants}
          initial="initial"
          animate="animate"
        >
          {/* Header */}
          <motion.div variants={itemVariants}>
            <Box sx={{ textAlign: 'center', mb: 6 }}>
              <Avatar
                sx={{
                  width: 80,
                  height: 80,
                  mx: 'auto',
                  mb: 2,
                  background: 'linear-gradient(135deg, #1e88e5 0%, #42a5f5 100%)',
                }}
              >
                <SportsFootball fontSize="large" />
              </Avatar>
              <Typography
                variant="h3"
                sx={{
                  fontWeight: 700,
                  mb: 1,
                  background: 'linear-gradient(135deg, #42a5f5, #66bb6a)',
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                }}
              >
                Choose Your Team
              </Typography>
              <Typography variant="h6" color="text.secondary">
                Welcome, {user?.username}! Select the team you are managing in game.
              </Typography>
            </Box>
          </motion.div>

          {/* Search and Filter Section */}
          <motion.div variants={itemVariants}>
            <GlassCard sx={{ mb: 4, p: 3 }}>
              <Stack spacing={3}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <FilterList color="primary" />
                  <Typography variant="h6" sx={{ fontWeight: 600 }}>
                    Find Your Team
                  </Typography>
                  <Chip 
                    label={`${filteredTeams.length} teams`} 
                    size="small" 
                    color="primary" 
                    variant="outlined"
                  />
                </Box>

                <Divider sx={{ opacity: 0.3 }} />

                <Grid container spacing={3}>
                  {/* Search Field */}
                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      placeholder="Search teams, coaches, or conferences..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      InputProps={{
                        startAdornment: (
                          <InputAdornment position="start">
                            <Search color="action" />
                          </InputAdornment>
                        ),
                        sx: {
                          backgroundColor: 'rgba(255,255,255,0.05)',
                          '& .MuiOutlinedInput-notchedOutline': {
                            borderColor: 'rgba(255,255,255,0.2)',
                          },
                          '&:hover .MuiOutlinedInput-notchedOutline': {
                            borderColor: 'rgba(255,255,255,0.3)',
                          },
                        },
                      }}
                      sx={{
                        '& .MuiInputLabel-root': {
                          color: 'text.secondary',
                        },
                      }}
                    />
                  </Grid>

                  {/* Conference Filter */}
                  <Grid item xs={12} md={4}>
                    <FormControl fullWidth>
                      <InputLabel sx={{ color: 'text.secondary' }}>Conference</InputLabel>
                      <Select
                        value={selectedConference}
                        onChange={(e) => setSelectedConference(e.target.value)}
                        label="Conference"
                        sx={{
                          backgroundColor: 'rgba(255,255,255,0.05)',
                          '& .MuiOutlinedInput-notchedOutline': {
                            borderColor: 'rgba(255,255,255,0.2)',
                          },
                          '&:hover .MuiOutlinedInput-notchedOutline': {
                            borderColor: 'rgba(255,255,255,0.3)',
                          },
                        }}
                      >
                        <MenuItem value="All">All Conferences</MenuItem>
                        {conferences.map((conference) => (
                          <MenuItem key={conference} value={conference}>
                            {conference}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>

                  {/* Human Teams Filter */}
                  <Grid item xs={12} md={2}>
                    <FormControl fullWidth>
                      <InputLabel sx={{ color: 'text.secondary' }}>Type</InputLabel>
                      <Select
                        value={showHumanOnly ? 'human' : 'all'}
                        onChange={(e) => setShowHumanOnly(e.target.value === 'human')}
                        label="Type"
                        sx={{
                          backgroundColor: 'rgba(255,255,255,0.05)',
                          '& .MuiOutlinedInput-notchedOutline': {
                            borderColor: 'rgba(255,255,255,0.2)',
                          },
                          '&:hover .MuiOutlinedInput-notchedOutline': {
                            borderColor: 'rgba(255,255,255,0.3)',
                          },
                        }}
                      >
                        <MenuItem value="all">All Teams</MenuItem>
                        <MenuItem value="human">Human Only</MenuItem>
                      </Select>
                    </FormControl>
                  </Grid>
                </Grid>
              </Stack>
            </GlassCard>
          </motion.div>

          {/* Team Grid */}
          <Grid container spacing={3}>
            {teams.map((team, index) => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={team.id}>
                <motion.div
                  variants={itemVariants}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                >
                  <Card
                    onClick={() => handleTeamSelect(team)}
                    sx={{
                      cursor: 'pointer',
                      background: selectedTeam?.id === team.id
                        ? 'linear-gradient(145deg, rgba(30,136,229,0.2) 0%, rgba(66,165,245,0.1) 100%)'
                        : 'linear-gradient(145deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%)',
                      backdropFilter: 'blur(20px)',
                      border: selectedTeam?.id === team.id
                        ? '2px solid #42a5f5'
                        : '1px solid rgba(255,255,255,0.1)',
                      borderRadius: '20px',
                      boxShadow: selectedTeam?.id === team.id
                        ? '0 8px 32px rgba(66,165,245,0.3)'
                        : '0 8px 32px rgba(0,0,0,0.3)',
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        transform: 'translateY(-4px)',
                        boxShadow: '0 12px 40px rgba(0,0,0,0.4)',
                      },
                    }}
                  >
                    <CardContent sx={{ p: 3, textAlign: 'center' }}>
                      {/* Team Avatar */}
                      <Avatar
                        sx={{
                          width: 60,
                          height: 60,
                          mx: 'auto',
                          mb: 2,
                          background: 'linear-gradient(135deg, #ff9800 0%, #ffb74d 100%)',
                          fontSize: '1.5rem',
                          fontWeight: 700,
                        }}
                      >
                        {team.name.substring(0, 2).toUpperCase()}
                      </Avatar>

                      {/* Team Name */}
                      <Typography
                        variant="h6"
                        sx={{
                          fontWeight: 600,
                          mb: 1,
                          color: selectedTeam?.id === team.id ? '#42a5f5' : 'text.primary',
                        }}
                      >
                        {team.name}
                      </Typography>

                      {/* Conference */}
                      {team.conference && (
                        <Chip
                          label={team.conference}
                          size="small"
                          sx={{
                            mb: 2,
                            background: 'linear-gradient(135deg, rgba(255,255,255,0.2), rgba(255,255,255,0.1))',
                            color: 'text.secondary',
                          }}
                        />
                      )}

                      {/* Team Stats */}
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
                        <Box sx={{ textAlign: 'left' }}>
                          <Typography variant="body2" color="text.secondary">
                            Record
                          </Typography>
                          <Typography variant="body1" sx={{ fontWeight: 600 }}>
                            {team.currentWins || 0}-{team.currentLosses || 0}
                          </Typography>
                        </Box>
                        <Box sx={{ textAlign: 'right' }}>
                          <Typography variant="body2" color="text.secondary">
                            Rank
                          </Typography>
                          <Typography variant="body1" sx={{ fontWeight: 600 }}>
                            {team.currentRank ? `#${team.currentRank}` : 'NR'}
                          </Typography>
                        </Box>
                      </Box>

                      {/* Human Team Indicator */}
                      {team.isHuman && (
                        <Box sx={{ mt: 2 }}>
                          <Chip
                            icon={<Person />}
                            label="Human Controlled"
                            size="small"
                            color="primary"
                            sx={{
                              background: 'linear-gradient(135deg, #1e88e5, #42a5f5)',
                              color: 'white',
                            }}
                          />
                        </Box>
                      )}

                      {/* Selection Indicator */}
                      {selectedTeam?.id === team.id && (
                        <Box sx={{ mt: 2 }}>
                          <Star color="primary" />
                        </Box>
                      )}
                    </CardContent>
                  </Card>
                </motion.div>
              </Grid>
            ))}
          </Grid>

          {/* Confirm Button */}
          {selectedTeam && (
            <motion.div
              variants={itemVariants}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.3 }}
            >
              <Box sx={{ textAlign: 'center', mt: 6 }}>
                <GlassCard hover={false} sx={{ display: 'inline-block', p: 3 }}>
                  <Typography variant="h6" sx={{ mb: 2 }}>
                    Selected: {selectedTeam.name}
                  </Typography>
                  <GradientButton
                    size="large"
                    onClick={handleConfirmSelection}
                    disabled={isSubmitting}
                  >
                    {isSubmitting ? 'Setting up...' : 'Start Managing Team'}
                  </GradientButton>
                </GlassCard>
              </Box>
            </motion.div>
          )}
        </motion.div>
      </Container>
    </Box>
  );
};

export default TeamSelection;
