import React from 'react';
import {
  Grid,
  Typography,
  Box,
  Chip,
  Avatar,
  LinearProgress,
  CircularProgress,
} from '@mui/material';
import {
  SportsFootball,
  EmojiEvents,
  TrendingUp,
  Schedule,
} from '@mui/icons-material';
import { motion } from 'framer-motion';
import { useQuery } from '@tanstack/react-query';
import { format } from 'date-fns';
import GlassCard from '../components/ui/GlassCard';
import GradientButton from '../components/ui/GradientButton';
import { teamApi, gameApi, standingApi } from '../services/api';
import { useAuth } from '../hooks/useAuth';

const Dashboard: React.FC = () => {
  const { selectedTeam, user } = useAuth();

  // API Queries - now using the selected team
  const { data: recentGamesData, isLoading: recentGamesLoading } = useQuery({
    queryKey: ['games', 'recent', selectedTeam?.id],
    queryFn: () => selectedTeam ? gameApi.getByTeam(selectedTeam.id) : Promise.resolve({ data: [] }),
    enabled: !!selectedTeam,
  });

  const { data: upcomingGamesData, isLoading: upcomingGamesLoading } = useQuery({
    queryKey: ['games', 'upcoming', selectedTeam?.id],
    queryFn: () => selectedTeam ? gameApi.getUpcoming(selectedTeam.id) : Promise.resolve({ data: [] }),
    enabled: !!selectedTeam,
  });

  const { data: standings, isLoading: standingsLoading } = useQuery({
    queryKey: ['standings', new Date().getFullYear()],
    queryFn: () => standingApi.getByYear(new Date().getFullYear()),
  });

  const containerVariants = {
    initial: { opacity: 0 },
    animate: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
      },
    },
  };

  const itemVariants = {
    initial: { y: 20, opacity: 0 },
    animate: {
      y: 0,
      opacity: 1,
      transition: {
        type: 'spring',
        stiffness: 300,
        damping: 24,
      },
    },
  };

  // Calculate stats from API data using selected team
  const userStanding = standings?.data?.find(s => s.teamId === selectedTeam?.id);
  const nextGame = upcomingGamesData?.data?.[0];

  const statCards = [
    {
      title: 'Current Rank',
      value: userStanding?.rank ? `#${userStanding.rank}` : '--',
      icon: TrendingUp,
      color: '#4caf50',
      gradient: 'success',
      subtitle: 'National Ranking',
    },
    {
      title: 'Season Record',
      value: userStanding ? `${userStanding.wins}-${userStanding.losses}` : '--',
      icon: SportsFootball,
      color: '#1e88e5',
      gradient: 'primary',
      subtitle: 'Win-Loss Record',
    },
    {
      title: 'Conference',
      value: selectedTeam?.conference || '--',
      icon: EmojiEvents,
      color: '#ff9800',
      gradient: 'warning',
      subtitle: 'Division',
    },
    {
      title: 'Next Game',
      value: nextGame ? 'Scheduled' : 'TBD',
      icon: Schedule,
      color: '#26a69a',
      gradient: 'secondary',
      subtitle: nextGame 
        ? `vs ${nextGame.awayTeamName || nextGame.homeTeamName}`
        : 'No upcoming games',
    },
  ];

  // Process recent games data
  const recentGames = recentGamesData?.data?.slice(0, 4).map(game => {
    const isHome = game.homeTeamId === selectedTeam?.id;
    const opponent = isHome ? game.awayTeamName : game.homeTeamName;
    const userScore = isHome ? game.homeScore : game.awayScore;
    const opponentScore = isHome ? game.awayScore : game.homeScore;

    let result = 'T';
    if (userScore > opponentScore) result = 'W';
    else if (userScore < opponentScore) result = 'L';

    return {
      opponent: opponent || 'Unknown',
      score: `${userScore}-${opponentScore}`,
      result,
      date: game.date ? format(new Date(game.date), 'MMM d') : 'TBD',
    };
  }).filter(Boolean) || [];

  // Process upcoming games data
  const upcomingGames = upcomingGamesData?.data?.slice(0, 2).map(game => {
    const isHome = game.homeTeamId === humanTeam?.id;
    const opponent = isHome ? game.awayTeamName : game.homeTeamName;
    const opponentRank = isHome ? game.awayTeamRank : game.homeTeamRank;

    return {
      opponent: opponent || 'Unknown',
      date: game.date ? format(new Date(game.date), 'MMM d') : 'TBD',
      time: game.date ? format(new Date(game.date), 'h:mm a') : 'TBD',
      rank: opponentRank ? `#${opponentRank}` : 'NR',
    };
  }).filter(Boolean) || [];

  // Calculate season progress metrics
  const totalGames = userStanding ? userStanding.totalGames : 0;
  const bowlEligible = (userStanding?.wins || 0) >= 6;
  const winPercentage = userStanding?.winPercentage || 0;
  const currentRank = userStanding?.rank || 0;
  // For conference position, we'll use a simplified calculation since we don't have conference standings
  const conferencePosition = currentRank > 0 ? Math.max(0, 100 - (currentRank * 2)) : 0;

  return (
    <motion.div
      variants={containerVariants}
      initial="initial"
      animate="animate"
    >
      <Box sx={{ mb: 4 }}>
        <motion.div variants={itemVariants}>
          <Typography
            variant="h3"
            sx={{
              fontWeight: 700,
              mb: 1,
              background: 'linear-gradient(135deg, #42a5f5, #66bb6a)',
              backgroundClip: 'text',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              pt: 7
            }}
          >
            Dashboard
          </Typography>
          <Typography variant="h6" color="text.secondary">
            Welcome back, Coach! Here's your team overview.
          </Typography>
        </motion.div>
      </Box>

      <Grid container spacing={3}>
        {/* Stat Cards */}
        {(teamsLoading || standingsLoading) ? (
          Array.from({ length: 4 }).map((_, index) => (
            <Grid item xs={12} sm={6} lg={3} key={index}>
              <motion.div variants={itemVariants}>
                <GlassCard gradient>
                  <Box sx={{ p: 3, display: 'flex', justifyContent: 'center', alignItems: 'center', height: 120 }}>
                    <CircularProgress />
                  </Box>
                </GlassCard>
              </motion.div>
            </Grid>
          ))
        ) : statCards.map((stat, index) => (
          <Grid item xs={12} sm={6} lg={3} key={stat.title}>
            <motion.div variants={itemVariants}>
              <GlassCard gradient>
                <Box sx={{ p: 3 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                    <Avatar
                      sx={{
                        background: `linear-gradient(135deg, ${stat.color}22, ${stat.color}44)`,
                        color: stat.color,
                        mr: 2,
                      }}
                    >
                      <stat.icon />
                    </Avatar>
                    <Box>
                      <Typography variant="h4" sx={{ fontWeight: 700 }}>
                        {stat.value}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {stat.subtitle}
                      </Typography>
                    </Box>
                  </Box>
                  <Typography variant="h6" color="text.primary">
                    {stat.title}
                  </Typography>
                </Box>
              </GlassCard>
            </motion.div>
          </Grid>
        ))}

        {/* Recent Games */}
        <Grid item xs={12} lg={6}>
          <motion.div variants={itemVariants}>
            <GlassCard>
              <Box sx={{ p: 3 }}>
                <Typography variant="h5" sx={{ mb: 3, fontWeight: 600 }}>
                  Recent Games
                </Typography>
                {recentGamesLoading ? (
                  <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
                    <CircularProgress />
                  </Box>
                ) : recentGames.length === 0 ? (
                  <Typography variant="body2" color="text.secondary" sx={{ textAlign: 'center', py: 4 }}>
                    No recent games found
                  </Typography>
                ) : recentGames.map((game, index) => (
                  <Box
                    key={index}
                    sx={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-between',
                      py: 2,
                      borderBottom: index < recentGames.length - 1 ? '1px solid rgba(255,255,255,0.1)' : 'none',
                    }}
                  >
                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                      <Chip
                        label={game.result}
                        size="small"
                        sx={{
                          background: game.result === 'W' 
                            ? 'linear-gradient(135deg, #4caf50, #66bb6a)'
                            : 'linear-gradient(135deg, #f44336, #e57373)',
                          color: 'white',
                          fontWeight: 600,
                          mr: 2,
                        }}
                      />
                      <Typography variant="body1" sx={{ fontWeight: 600 }}>
                        vs {game.opponent}
                      </Typography>
                    </Box>
                    <Box sx={{ textAlign: 'right' }}>
                      <Typography variant="body1" sx={{ fontWeight: 600 }}>
                        {game.score}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {game.date}
                      </Typography>
                    </Box>
                  </Box>
                ))}
              </Box>
            </GlassCard>
          </motion.div>
        </Grid>

        {/* Upcoming Games */}
        <Grid item xs={12} lg={6}>
          <motion.div variants={itemVariants}>
            <GlassCard>
              <Box sx={{ p: 3 }}>
                <Typography variant="h5" sx={{ mb: 3, fontWeight: 600 }}>
                  Upcoming Games
                </Typography>
                {upcomingGamesLoading ? (
                  <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
                    <CircularProgress />
                  </Box>
                ) : upcomingGames.length === 0 ? (
                  <Typography variant="body2" color="text.secondary" sx={{ textAlign: 'center', py: 4 }}>
                    No upcoming games scheduled
                  </Typography>
                ) : upcomingGames.map((game, index) => (
                  <Box
                    key={index}
                    sx={{
                      p: 2,
                      mb: index < upcomingGames.length - 1 ? 2 : 0,
                      background: 'linear-gradient(135deg, rgba(255,255,255,0.1), rgba(255,255,255,0.05))',
                      borderRadius: '12px',
                      border: '1px solid rgba(255,255,255,0.1)',
                    }}
                  >
                    <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                      <Box>
                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                          <Typography variant="h6" sx={{ fontWeight: 600, mr: 1 }}>
                            vs {game.opponent}
                          </Typography>
                          <Chip
                            label={game.rank}
                            size="small"
                            sx={{
                              background: 'linear-gradient(135deg, #ff9800, #ffb74d)',
                              color: 'white',
                              fontWeight: 600,
                            }}
                          />
                        </Box>
                        <Typography variant="body2" color="text.secondary">
                          {game.date} • {game.time}
                        </Typography>
                      </Box>
                      <GradientButton size="small">
                        View Details
                      </GradientButton>
                    </Box>
                  </Box>
                ))}
              </Box>
            </GlassCard>
          </motion.div>
        </Grid>

        {/* Team Progress */}
        <Grid item xs={12}>
          <motion.div variants={itemVariants}>
            <GlassCard>
              <Box sx={{ p: 3 }}>
                <Typography variant="h5" sx={{ mb: 3, fontWeight: 600 }}>
                  Season Progress
                </Typography>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={4}>
                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body1" sx={{ mb: 1 }}>
                        National Ranking
                      </Typography>
                      <LinearProgress
                        variant="determinate"
                        value={conferencePosition}
                        sx={{
                          height: 8,
                          borderRadius: 4,
                          background: 'rgba(255,255,255,0.1)',
                          '& .MuiLinearProgress-bar': {
                            background: 'linear-gradient(90deg, #1e88e5, #42a5f5)',
                            borderRadius: 4,
                          },
                        }}
                      />
                      <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                        {currentRank > 0 ? `#${currentRank} nationally` : 'Unranked'}
                      </Typography>
                    </Box>
                  </Grid>
                  <Grid item xs={12} md={4}>
                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body1" sx={{ mb: 1 }}>
                        Bowl Eligibility
                      </Typography>
                      <LinearProgress
                        variant="determinate"
                        value={bowlEligible ? 100 : ((userStanding?.wins || 0) / 6) * 100}
                        sx={{
                          height: 8,
                          borderRadius: 4,
                          background: 'rgba(255,255,255,0.1)',
                          '& .MuiLinearProgress-bar': {
                            background: bowlEligible 
                              ? 'linear-gradient(90deg, #4caf50, #66bb6a)'
                              : 'linear-gradient(90deg, #ff9800, #ffb74d)',
                            borderRadius: 4,
                          },
                        }}
                      />
                      <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                        {bowlEligible ? 'Qualified' : `${userStanding?.wins || 0}/6 wins`}
                      </Typography>
                    </Box>
                  </Grid>
                  <Grid item xs={12} md={4}>
                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body1" sx={{ mb: 1 }}>
                        Win Percentage
                      </Typography>
                      <LinearProgress
                        variant="determinate"
                        value={winPercentage * 100}
                        sx={{
                          height: 8,
                          borderRadius: 4,
                          background: 'rgba(255,255,255,0.1)',
                          '& .MuiLinearProgress-bar': {
                            background: winPercentage >= 0.75 
                              ? 'linear-gradient(90deg, #4caf50, #66bb6a)'
                              : winPercentage >= 0.5
                              ? 'linear-gradient(90deg, #ff9800, #ffb74d)'
                              : 'linear-gradient(90deg, #f44336, #e57373)',
                            borderRadius: 4,
                          },
                        }}
                      />
                      <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                        {(winPercentage * 100).toFixed(1)}% this season
                      </Typography>
                    </Box>
                  </Grid>
                </Grid>
              </Box>
            </GlassCard>
          </motion.div>
        </Grid>
      </Grid>
    </motion.div>
  );
};

export default Dashboard;
