import React from 'react';
import {
  Grid,
  Typography,
  Box,
  Chip,
  Avatar,
  LinearProgress,
  CircularProgress,
} from '@mui/material';
import {
  SportsFootball,
  EmojiEvents,
  TrendingUp,
  Schedule,
} from '@mui/icons-material';
import { motion } from 'framer-motion';
import { useQuery } from '@tanstack/react-query';
import { format } from 'date-fns';
import GlassCard from '../components/ui/GlassCard';
import GradientButton from '../components/ui/GradientButton';
import { teamApi, gameApi, standingApi } from '../services/api';

const Dashboard: React.FC = () => {
  // API Queries
  const { data: teams, isLoading: teamsLoading } = useQuery({
    queryKey: ['teams', 'human'],
    queryFn: () => teamApi.getHumanTeams(),
  });

  const { data: recentGamesData, isLoading: recentGamesLoading } = useQuery({
    queryKey: ['games', 'recent'],
    queryFn: () => gameApi.getAll({ page: 0, size: 4 }),
  });

  const { data: upcomingGamesData, isLoading: upcomingGamesLoading } = useQuery({
    queryKey: ['games', 'upcoming'],
    queryFn: () => gameApi.getUpcoming(),
  });

  const { data: standings, isLoading: standingsLoading } = useQuery({
    queryKey: ['standings', new Date().getFullYear()],
    queryFn: () => standingApi.getByYear(new Date().getFullYear()),
  });

  const containerVariants = {
    initial: { opacity: 0 },
    animate: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
      },
    },
  };

  const itemVariants = {
    initial: { y: 20, opacity: 0 },
    animate: {
      y: 0,
      opacity: 1,
      transition: {
        type: 'spring',
        stiffness: 300,
        damping: 24,
      },
    },
  };

  const statCards = [
    {
      title: 'Current Rank',
      value: '#12',
      icon: TrendingUp,
      color: '#4caf50',
      gradient: 'success',
      subtitle: 'National Ranking',
    },
    {
      title: 'Season Record',
      value: '8-2',
      icon: SportsFootball,
      color: '#1e88e5',
      gradient: 'primary',
      subtitle: 'Win-Loss Record',
    },
    {
      title: 'Achievements',
      value: '15',
      icon: EmojiEvents,
      color: '#ff9800',
      gradient: 'warning',
      subtitle: 'Total Unlocked',
    },
    {
      title: 'Next Game',
      value: '2 Days',
      icon: Schedule,
      color: '#26a69a',
      gradient: 'secondary',
      subtitle: 'vs Alabama',
    },
  ];

